// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import { Ownable } from "@openzeppelin/contracts/access/Ownable.sol";
import { IERC20, SafeERC20 } from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import { OFTAdapter } from "@layerzerolabs/oft-evm/contracts/OFTAdapter.sol";
import { OFTCore } from "@layerzerolabs/oft-evm/contracts/OFTCore.sol";
import { SendParam, OFTLimit, OFTFeeDetail, OFTReceipt } from "@layerzerolabs/oft-evm/contracts/interfaces/IOFT.sol";
import { RateLimiter } from "@layerzerolabs/oapp-evm/contracts/oapp/utils/RateLimiter.sol";

/**
 * @title MorphoOFTAdapter
 * @notice OFT adapter with outbound rate limiting.
 */
contract MorphoOFTAdapter is OFTAdapter, RateLimiter {
    using SafeERC20 for IERC20;

    constructor(
        address _token,
        address _lzEndpoint,
        address _delegate,
        RateLimitConfig[] memory _rateLimitConfigs
    ) OFTAdapter(_token, _lzEndpoint, _delegate) Ownable(_delegate) {
        _setRateLimits(_rateLimitConfigs);
    }

    /**
     * @notice Set an array of rate limit configurations.
     * @param _rateLimitConfigs Rate limit configurations
     */
    function setRateLimits(RateLimitConfig[] memory _rateLimitConfigs) external onlyOwner {
        _setRateLimits(_rateLimitConfigs);
    }

    /**
     * @notice Resets the rate limits (sets amountInFlight to 0) for the given endpoint ids.
     * @param _eids The endpoint ids to reset the rate limits for.
     */
    function resetRateLimits(uint32[] memory _eids) external onlyOwner {
        _resetRateLimits(_eids);
    }

    /**
     * @dev Override to apply outbound rate limit.
     * @inheritdoc OFTAdapter
     */
    function _debit(
        address _from,
        uint256 _amountLD,
        uint256 _minAmountLD,
        uint32 _dstEid
    ) internal virtual override returns (uint256 amountSentLD, uint256 amountReceivedLD) {
        (amountSentLD, amountReceivedLD) = _debitView(_amountLD, _minAmountLD, _dstEid);
        /// @dev Apply outbound rate limit for the destination EID.
        _outflow(_dstEid, amountReceivedLD);
        innerToken.safeTransferFrom(_from, address(this), amountSentLD);
    }

    /**
     * @dev Override to get the max amount that can be sent due to rate limits.
     *      Only the `maxAmountLD` value is overridden, the rest remains unchanged.
     * @inheritdoc OFTCore
     */
    function quoteOFT(
        SendParam calldata _sendParam
    )
        external
        view
        virtual
        override
        returns (OFTLimit memory oftLimit, OFTFeeDetail[] memory oftFeeDetails, OFTReceipt memory oftReceipt)
    {
        uint256 minAmountLD = 0;

        /// @dev Override `maxAmountLD`.
        (, uint256 maxAmountLD) = getAmountCanBeSent(_sendParam.dstEid);
        oftLimit = OFTLimit(minAmountLD, maxAmountLD);

        oftFeeDetails = new OFTFeeDetail[](0);

        (uint256 amountSentLD, uint256 amountReceivedLD) = _debitView(
            _sendParam.amountLD,
            _sendParam.minAmountLD,
            _sendParam.dstEid
        );
        oftReceipt = OFTReceipt(amountSentLD, amountReceivedLD);
    }
}

contracts/MorphoToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.22;

import { ERC20Upgradeable } from "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import { AccessControlEnumerableUpgradeable } from "@openzeppelin/contracts-upgradeable/access/extensions/AccessControlEnumerableUpgradeable.sol";
import { UUPSUpgradeable } from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import { ERC1967Utils } from "@openzeppelin/contracts/proxy/ERC1967/ERC1967Utils.sol";
import { IMintableBurnable } from "@layerzerolabs/oft-evm/contracts/interfaces/IMintableBurnable.sol";

contract MorphoToken is ERC20Upgradeable, AccessControlEnumerableUpgradeable, UUPSUpgradeable, IMintableBurnable {
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant BURNER_ROLE = keccak256("BURNER_ROLE");
    bytes32 public constant UPGRADER_ROLE = keccak256("UPGRADER_ROLE");

    constructor() {
        _disableInitializers();
    }

    /**
     * @notice Initializes the token contract.
     * @param _name Name of the token
     * @param _symbol Symbol of the token
     * @param _owner Initial default admin of the contract
     */
    function initialize(string memory _name, string memory _symbol, address _owner) external initializer {
        __ERC20_init(_name, _symbol);
        __AccessControlEnumerable_init();
        __UUPSUpgradeable_init();
        _grantRole(DEFAULT_ADMIN_ROLE, _owner);
    }

    /**
     * @notice Mints a specific amount of tokens to a given address.
     * @param _to The address to which tokens will be minted
     * @param _amount The amount of tokens to mint
     * @return A boolean indicating the success of the mint operation
     */
    function mint(address _to, uint256 _amount) external onlyRole(MINTER_ROLE) returns (bool) {
        _mint(_to, _amount);
        return true;
    }

    /**
     * @notice Burns a specific amount of tokens from a given address.
     * @param _from The address from which tokens will be burned
     * @param _amount The amount of tokens to burn
     * @return A boolean indicating the success of the burn operation
     */
    function burn(address _from, uint256 _amount) external onlyRole(BURNER_ROLE) returns (bool) {
        _burn(_from, _amount);
        return true;
    }

    /// @notice Returns the contract's current implementation address.
    function getImplementation() external view returns (address) {
        return ERC1967Utils.getImplementation();
    }

    /// @inheritdoc UUPSUpgradeable
    function _authorizeUpgrade(address) internal override onlyRole(UPGRADER_ROLE) {}
}

